package com.vic.project.app_maps.diimport android.app.Applicationimport android.content.Contextimport com.vic.project.app_maps.data.repository.LocationRepositoryImplimport com.vic.project.app_maps.domain.repository.LocationRepositoryimport com.vic.project.app_maps.network.ConnectivityManagerNetworkMonitorimport com.vic.project.app_maps.network.NetworkMonitorimport dagger.Bindsimport dagger.Moduleimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)interface DataBindModule {    @Singleton    @Binds    fun provideContext(application: Application): Context    @Binds    fun bindsNetworkMonitor(        networkMonitor: ConnectivityManagerNetworkMonitor,    ): NetworkMonitor    @Singleton    @Binds    fun bindLocationRepository(        impl: LocationRepositoryImpl    ): LocationRepository}