package com.vic.project.app_maps.domain.repositoryimport com.google.android.gms.maps.model.LatLngimport com.vic.project.app_maps.data.model.LatLongimport com.vic.project.app_maps.data.remote.model.response.BaseResponseimport com.vic.project.app_maps.data.remote.model.response.ResultWrapperimport kotlinx.coroutines.flow.Flowinterface LocationRepository {    fun getCurrentLocation(): Flow<LatLong>    fun getRealTimeLocation(current: LatLng): Flow<LatLong>    fun getPlaceRecommend(address: String): Flow<ResultWrapper<BaseResponse>>    fun searchPlace(address: String): Flow<ResultWrapper<BaseResponse>>    fun getDirections(start: LatLng?, end: LatLng?, model: String): Flow<ResultWrapper<BaseResponse>>    fun getListHistory(): List<String>    fun addHistory(data: String)}