package com.vic.project.app_maps.utilsimport android.Manifestimport android.content.Contextimport android.content.pm.PackageManagerimport androidx.core.content.ContextCompatobject ContextUtils {    fun Context.hasFineLocation(): Boolean = ContextCompat.checkSelfPermission(        this, Manifest.permission.ACCESS_FINE_LOCATION    ) == PackageManager.PERMISSION_GRANTED    fun Context.hasCoarseLocation(): Boolean = ContextCompat.checkSelfPermission(        this, Manifest.permission.ACCESS_COARSE_LOCATION    ) == PackageManager.PERMISSION_GRANTED}